 # S7 1112","              # any other binary device","             device_id in self.binaryDevices or","              # special device","             device_id in self.specialDevices","             )","","    def get_container_weight(self, container_idx:int) -\u003e Optional[int]:","        if len(self.container_weights) \u003e container_idx \u003e= 0:","            return self.container_weights[container_idx]","        return None","","    # toggles the y cursor coordinate see self.fmt_data_curve","    def nextFmtDataCurve(self) -\u003e None:","        self.fmt_data_curve = (self.fmt_data_curve+1) % 5","        if self.backgroundprofile is None and self.fmt_data_curve in {3, 4}:","            self.fmt_data_curve = 0","        if len(self.timex)\u003c3 and self.fmt_data_curve in {1, 2}:","            if self.backgroundprofile is None:","                self.fmt_data_curve = 0","            else:","                self.fmt_data_curve = 3","        s = 'cursor position'","        if self.fmt_data_curve == 1:","            s = self.aw.BTname","        elif self.fmt_data_curve == 2:","            s = self.aw.ETname","        elif self.fmt_data_curve == 3:","            s = f\"{QApplication.translate('Label','Background')} {self.aw.BTname}\"","        elif self.fmt_data_curve == 4:","            s = f\"{QApplication.translate('Label','Background')} {self.aw.ETname}\"","        self.aw.ntb.update_message()","        self.aw.sendmessage(QApplication.translate('Message', 'set y-coordinate to {}').format(s))","","    @pyqtSlot(str, bool)","    def showCurve(self, name: str, state: bool) -\u003e None:","        changed:bool = False","        if name == 'ET' and self.ETcurve != state:","            self.ETcurve = state","            changed = True","        elif name == 'BT' and self.BTcurve != state:","            self.BTcurve = state","            changed = True","        elif name == 'DeltaET' and self.DeltaETflag != state:","            self.DeltaETflag = state","            changed = True","        elif name == 'DeltaBT' and self.DeltaBTflag != state:","            self.DeltaBTflag = state","            changed = True","        elif name == 'BackgroundET' and self.backgroundETcurve != state:","            self.backgroundETcurve = state","            changed = True","        elif name == 'BackgroundBT' and self.backgroundBTcurve != state:","            self.backgroundBTcurve = state","            changed = True","        if changed:","            self.redraw(recomputeAllDeltas=False,re_smooth_foreground=False)","","    @pyqtSlot(int, str, bool)","    def showExtraCurve(self, extra_device: int, curve: str, state: bool) -\u003e None:","        assert self.aw is not None","        if curve.strip() == 'T1' and len(self.aw.extraCurveVisibility1) \u003e extra_device and self.aw.extraCurveVisibility1[extra_device] != state:","            self.aw.extraCurveVisibility1[extra_device] = state","            self.redraw(recomputeAllDeltas=False,re_smooth_foreground=False)","        elif curve.strip() == 'T2' and len(self.aw.extraCurveVisibility2) \u003e extra_device and self.aw.extraCurveVisibility2[extra_device] != state:","            self.aw.extraCurveVisibility2[extra_device] = state","            self.redraw(recomputeAllDeltas=False,re_smooth_foreground=False)","","    @pyqtSlot(int, bool)","    def showEvents(self, event_type: int, state: bool) -\u003e None:","        event_type -= 1","        if len(self.showEtypes) \u003e event_type \u003e= 0 and self.showEtypes[event_type] != state:","            self.showEtypes[event_type] = state","            self.redraw(recomputeAllDeltas=False,re_smooth_foreground=False)","","    @pyqtSlot(bool)","    def showBackgroundEvents(self, state: bool) -\u003e None:","        if state != self.backgroundeventsflag:","            self.backgroundeventsflag = state","            self.redraw(recomputeAllDeltas=False,re_smooth_foreground=False)","","    def ax_lines_clear(self) -\u003e None:","        if self.ax is not None:","            while len(self.ax.lines) \u003e 0:","                self.ax.lines[0].remove()","","    def ax_combo_text_annotations_clear(self) -\u003e None:","        if self.ax is not None:","            for child in self.ax.get_children():","                if isinstance(child, Annotation):","                    try:","                        child.remove()","                    except Exception: # pylint: disable=broad-except","                        pass","","    def ax_annotations_clear(self) -\u003e None:","        for la in self.l_annotations + self.l_background_annotations:","            if la:","                try:","                    la.remove()","                except Exception: # pylint: disable=broad-except","                    pass","","    # set current burner settings as defaults","    def setEnergyLoadDefaults(self) -\u003e None:","        self.loadlabels_setup = self.loadlabels[:]","        self.loadratings_setup = self.loadratings[:]","        self.ratingunits_setup = self.ratingunits[:]","        self.sourcetypes_setup = self.sourcetypes[:]","        self.load_etypes_setup = self.load_etypes[:]","        self.presssure_percents_setup = self.presssure_percents[:]","        self.loadevent_zeropcts_setup = self.loadevent_zeropcts[:]","        self.loadevent_hundpcts_setup = self.loadevent_hundpcts[:]","        self.meterlabels_setup = self.meterlabels[:]","        self.meterunits_setup = self.meterunits[:]","        self.meterfuels_setup = self.meterfuels[:]","        self.metersources_setup = self.metersources[:]","        self.electricEnergyMix_setup = self.electricEnergyMix","        self.gasMix_setup = self.gasMix","","    # restore burner settings to their defaults","    def restoreEnergyLoadDefaults(self) -\u003e None:","        self.loadlabels = self.loadlabels_setup[:]","        self.loadratings = self.loadratings_setup[:]","        self.ratingunits = self.ratingunits_setup[:]","        self.sourcetypes = self.sourcetypes_setup[:]","        self.load_etypes = self.load_etypes_setup[:]","        self.presssure_percents = self.presssure_percents_setup[:]","        self.loadevent_zeropcts = self.loadevent_zeropcts_setup[:]","        self.loadevent_hundpcts = self.loadevent_hundpcts_setup[:]","        self.meterlabels = self.meterlabels_setup[:]","        self.meterunits = self.meterunits_setup[:]","        self.meterfuels = self.meterfuels_setup[:]","        self.metersources = self.metersources_setup[:]","        self.electricEnergyMix = self.electricEnergyMix_setup","        self.gasMix = self.gasMix_setup","","    # set current protocol settings as defaults","    def setEnergyProtocolDefaults(self) -\u003e None:","        self.preheatDuration_setup = self.preheatDuration","        self.preheatenergies_setup = self.preheatenergies[:]","        self.betweenbatchDuration_setup = self.betweenbatchDuration","        self.betweenbatchenergies_setup = self.betweenbatchenergies[:]","        self.coolingDuration_setup = self.coolingDuration","        self.coolingenergies_setup = self.coolingenergies[:]","        self.betweenbatch_after_preheat_setup = self.betweenbatch_after_preheat","","    # restore protocol settings to their defaults","    def restoreEnergyProtocolDefaults(self) -\u003e None:","        self.preheatDuration = self.preheatDuration_setup","        self.preheatenergies = self.preheatenergies_setup[:]","        self.betweenbatchDuration = self.betweenbatchDuration_setup","        self.betweenbatchenergies = self.betweenbatchenergies_setup[:]","        self.coolingDuration = self.coolingDuration_setup","        self.coolingenergies = self.coolingenergies_setup[:]","        self.betweenbatch_after_preheat = self.betweenbatch_after_preheat_setup","","    @pyqtSlot()","    def fileDirty(self) -\u003e None:","        self.safesaveflag = True","        self.aw.updateWindowTitle()","","    @pyqtSlot()","    def fileClean(self) -\u003e None:","        self.safesaveflag = False","        self.aw.updateWindowTitle()","","    @pyqtSlot()","    def lazyredraw_on_resize(self) -\u003e None:","        self.lazyredraw(recomputeAllDeltas=False)","","    def resizeEvent(self, event:'QResizeEvent') -\u003e None:","        super().resizeEvent(event) # type:ignore","        # we only trigger a redraw on resize if a watermark is displayed to fix its aspect ratio","        if self.aw.redrawOnResize and self.aw.logofilename != '':","            dw = event.size().width() - event.oldSize().width()   # width change","            dh = event.size().height() - event.oldSize().height() # height change","#            t = libtime.time()","#            # ensure that we redraw during resize only once per second","#            if self.resizeredrawing + 0.5 \u003c t and ((dw != 0) or (dh != 0)):","#                self.resizeredrawing = t","#                QTimer.singleShot(1, lazyredraw_on_resize)","            if ((dw != 0) or (dh != 0)):","                self.lazyredraw_on_resize_timer.start(10)","","","    # update the self.deltaBTspan and deltaETspan from the given sampling interval, self.deltaETsamples and self.deltaBTsamples","    # interval is expected in seconds (either from the profile on load or from the sampling interval set for recording)","    # both deltaBTsamples and deltaETsamples are at least one","    def updateDeltaSamples(self) -\u003e None:","        if self.flagstart or self.profile_sampling_interval is None:","            speed = self.timeclock.getBase()/1000","            interval = speed * (self.delay / 1000)","        else:","            interval = self.profile_sampling_interval","        self.deltaBTsamples = max(1,int(round(self.deltaBTspan / interval)))","        self.deltaETsamples = max(1,int(round(self.deltaETspan / interval)))","","    @pyqtSlot()","    def updateBackground(self) -\u003e None:","        if not self.block_update and self.ax is not None:","            try:","                self.updateBackgroundSemaphore.acquire(1)","                self.block_update = True","                self.doUpdate()","            finally:","                if self.updateBackgroundSemaphore.available() \u003c 1:","                    self.updateBackgroundSemaphore.release(1)","","    def doUpdate(self) -\u003e None:","        if not self.designerflag:","            self.resetlinecountcaches() # ensure that the line counts are up to date","            self.resetlines() # get rid of projection, cross lines and AUC line","","            try:","                with warnings.catch_warnings():","                    warnings.simplefilter('ignore')","                    self.fig.canvas.draw() # this triggers _draw_event(self,evt)","                #self.fig.canvas.draw_idle() # ask the canvas to kindly draw it self some time in the future when Qt thinks it is convenient","                # make sure that the GUI framework has a chance to run its event loop","                # and clear any GUI events.  This needs to be in a try/except block","                # because the default implementation of this method is to raise","                # NotImplementedError","                #self.fig.canvas.flush_events() # don't FLUSH event as this can lead to a second redraw started from within the same GUI thread and","                # causen a hang by the blocked semaphore","            except Exception as e: # pylint: disable=broad-except","                _log.exception(e)","","            if self.ax is not None:","                axfig = self.ax.get_figure()","                if axfig is not None and hasattr(self.fig.canvas,'copy_from_bbox'):","                    self.ax_background = self.fig.canvas.copy_from_bbox(axfig.bbox) # pyright: ignore[reportAttributeAccessIssue]","                    # we redraw the additional artists like the projection lines, the timeline and the AUC guide line","                    self.update_additional_artists()","                    self.fig.canvas.blit(axfig.bbox)","","        self.block_update = False","","    def device_name_subst(self, device_name:str) -\u003e str:","        try:","            return device_name.format(self.etypes[0],self.etypes[1],self.etypes[2],self.etypes[3],self.mode)","        except Exception: # pylint: disable=broad-except","            # substitution might fail if the label contains brackets like in \"t{FCS}\"","            return device_name","","    def get_etype_default(self, i:int, default_etypes_set:Optional[List[int]] = None) -\u003e str:","        etypes_set = (self.default_etypes_set if default_etypes_set is None else default_etypes_set)","        return (self.alt_etypesdefault[i] if etypes_set[i] else self.etypesdefault[i])","","    def get_etypes_defaults(self) -\u003e List[str]:","        return [self.get_etype_default(i) for i,_ in enumerate(self.etypesdefault)]","","    def getetypes(self) -\u003e List[str]:","        if len(self.etypes) == 4:","            self.etypes.append('--')","        return self.etypes","","    def etypesf(self, i:int) -\u003e str:","        if len(self.etypes) == 4:","            self.etypes.append('--')","        if i \u003e 4:","            return self.etypes[i-5]","        return self.etypes[i]","","    def Betypesf(self, i:int, prefix:bool = False) -\u003e str:","        if len(self.Betypes) == 4:","            self.Betypes.append('--')","        if prefix and i \u003c 4:","            return 'Background'+self.Betypes[i]","        return self.Betypes[i]","","    # returns the first letter as abbrev of the etype name if any","    # NOTE: that event type names should never be the empty string, but older Artisan versions allowed for this!","    @staticmethod","    def etypeAbbrev(etype_name:str) -\u003e str:","        return etype_name[:1]","","    def ambientTempSourceAvg(self) -\u003e Optional[float]:","        res:Optional[float] = None","        if self.ambientTempSource:","            try:","                start = 0","                end = len(self.temp1) - 1","                if self.timeindex[0] \u003e -1: # CHARGE","                    start = self.timeindex[0]","                if self.timeindex[6] \u003e 0: # DROP","                    end = self.timeindex[6]","                if self.ambientTempSource == 1: # from ET","                    res = float(numpy.mean([e for e in self.temp1[start:end] if e is not None and e != -1]))","                elif self.ambientTempSource == 2: # from BT","                    res = float(numpy.mean([e for e in self.temp2[start:end] if e is not None and e != -1]))","                elif self.ambientTempSource \u003e 2 and ((self.ambientTempSource - 3) \u003c (2*len(self.extradevices))):","                    # from an extra device","                    if (self.ambientTempSource)%2==0:","                        res = float(numpy.mean([e for e in self.extratemp2[(self.ambientTempSource - 3)//2][start:end] if e is not None and e != -1]))","                    else:","                        res = float(numpy.mean([e for e in self.extratemp1[(self.ambientTempSource - 3)//2][start:end] if e is not None and e != -1]))","            except Exception as ex: # pylint: disable=broad-except # the array to average over might get empty and mean thus invoking an exception","                _log.exception(ex)","        if res is not None:","            res = float2float(res)","        return res","","    def updateAmbientTempFromPhidgetModulesOrCurve(self) -\u003e None:","        if not self.ambientTempSource:","            AT_device = None","            try:","                AT_device = self.extradevices.index(36)","            except Exception: # pylint: disable=broad-except","                try:","                    AT_device = self.extradevices.index(60)","                except Exception: # pylint: disable=broad-except","                    pass","            if AT_device is not None:","                # 1048_AT channel #36, TMP1101_AT channel #60","                # we try to access that devices first channel to retrieve the temperature data","                try:","                    ser = self.aw.extraser[AT_device]","                    if ser.PhidgetTemperatureSensor is not None:","                        at = ser.PhidgetTemperatureSensor[0].getTemperature()","                        if self.mode == 'F':","                            at = float2float(fromCtoFstrict(at))","                        self.ambientTemp = float2float(at)","                except Exception as e: # pylint: disable=broad-except","                    _log.exception(e)","            # in case the AT channel of the 1048 or the TMP1101 is not used as extra device, we try to attach to it anyhow and read the temp off","            elif self.ambientTemp == 0.0 and self.device in {34, 58}: # Phidget 1048 or TMP1101 channel 4 (use internal temp)","                try:","                    if self.aw.ser.PhidgetTemperatureSensor is not None and self.aw.ser.PhidgetTemperatureSensor[0].getAttached():","                        from Phidget22.Devices.TemperatureSensor import TemperatureSensor as PhidgetTemperatureSensor # type: ignore","                        ambient = PhidgetTemperatureSensor()","                        ambient.setDeviceSerialNumber(self.aw.ser.PhidgetTemperatureSensor[0].getDeviceSerialNumber())","                        if self.device == 58:","                            ambient.setHubPort(self.aw.ser.PhidgetTemperatureSensor[0].getHubPort())","                        ambient.setChannel(4)","                        ambient.openWaitForAttachment(1000) # timeout in ms","                        if self.phidgetRemoteOnlyFlag:","                            libtime.sleep(.8)","                        else:","                            libtime.sleep(.5)","                        t = ambient.getTemperature()","                        if self.mode == 'F':","                            self.ambientTemp = float2float(fromCtoFstrict(t))","                        else:","                            self.ambientTemp = float2float(t)","                        if ambient.getAttached():","                            ambient.close()","                except Exception as e: # pylint: disable=broad-except","                    _log.exception(e)","        res = self.ambientTempSourceAvg()","        if res is not None and (isinstance(res, (float,int))) and not math.isnan(res):","            self.ambientTemp = float2float(float(res))","","    def updateAmbientTemp(self) -\u003e None:","        self.updateAmbientTempFromPhidgetModulesOrCurve()","        try:","            self.startPhidgetManager()","            self.getAmbientData()","        except Exception as e: # pylint: disable=broad-except","            _log.exception(e)","","    # eventsvalues maps the given internal event value v to an external event int value as displayed to the user as special event value","    # historicaly internal event values ranged from [1-11] and external event values from [0-10]","    #   that range was extended to 0-100 in later Artisan versions","    # v is expected to be float value of range [-11.0,11.0] or None (interpreted as 0)","    # negative values are not used as event values, but as step arguments in extra button definitions","    #   11.0 =\u003e 100","    #   10.1 =\u003e 91","    #   10.0 =\u003e 90","    #   1.1 =\u003e 1","    #   1.0 =\u003e 0","    #   0.5 =\u003e 0","    #     0 =\u003e 0","    #  -1.0 =\u003e 0","    #  -1.1 =\u003e -1","    # -10.0 =\u003e -90","    # -10.1 =\u003e -91","    # -11.0 =\u003e -100","    @staticmethod","    def eventsInternal2ExternalValue(v:Optional[float]) -\u003e int:","        if v is None:","            return 0","        if -1.0 \u003c= v \u003c= 1.0:","            return 0","        if v \u003c -1.0:","            return -(int(round(abs(v)*10)) - 10)","        return int(round(v*10)) - 10","","    # the inverse of eventsInternal2ExternalValue, converting an external to an internal event value","    # v from [-100,100]","    @staticmethod","    def eventsExternal2InternalValue(v:int) -\u003e float:","        if v == 0:","            return 0.","        if v \u003e= 1:","            return v/10. + 1.","        return v/10. - 1.","","    # eventsvalues maps the given number v to a string to be displayed to the user as special event value","    # v is expected to be float value of range [0-10]","    # negative values are mapped to \"\"","    # 0.1 to \"1\"","    # ..","    # 1.0 to \"10\"","    # ..","    # 10.0 to \"100\"","    def eventsvalues(self, v:float) -\u003e str:","        return str(self.eventsInternal2ExternalValue(v))","","    # 100.0 to \"10\" and 10.1 to \"1\"","    @staticmethod","    def eventsvaluesShort(v:float) -\u003e str:","        value = v*10. - 10.","        if value == -10:","            return '0'","        if value \u003c 0:","            return ''","        return str(int(round(value)))","","    # the inverse to eventsvalues above (string -\u003e value)","    def str2eventsvalue(self, s:str) -\u003e float:","        st = s.strip()","        if st is None or len(st) == 0:","            return -1","        return self.eventsExternal2InternalValue(int(st))","","    def fit_titles(self) -\u003e None:","        #truncate title and statistic line to width of axis system to avoid that the MPL canvas goes into miser mode","        try:","            if self.ax is not None:","                r = None","                try:","                    r = self.fig.canvas.get_renderer() # type: ignore # MPL fails on savePDF with 'FigureCanvasPdf' object has no attribute 'get_renderer'","                except Exception: # pylint: disable=broad-except","                    pass","                if r is None:","                    ax_width = self.ax.get_window_extent().width","                else:","                    ax_width = self.ax.get_window_extent(renderer=r).width","                ax_width_for_title = ax_width - self.background_title_width","                redraw =